import { combineReducers } from 'redux'
import {
    SELECT_GEOGRAPHY,
    SELECT_REGION
} from './actions'

function regional(state = [], action) {
  switch (action.type) {
    case SELECT_GEOGRAPHY:
      return [
        ...state,
        {
          text: action.text
        }
      ]
    case SELECT_GEOGRAPHY:
     return [
        ...state,
        {
        index: action.index
        }
     ]
    default:
      return state
  }
}

const capacityManagementApp = combineReducers({
  regional
})

export default capacityManagementApp
