import React from "react";
import * as am4core from "@amcharts/amcharts4/core";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
import * as am4maps from "@amcharts/amcharts4/maps";
import am4geodata_worldLow from "@amcharts/amcharts4-geodata/worldLow";
import am4geodata_usaLow from "@amcharts/amcharts4-geodata/usaLow";
import am4geodata_australiaLow from "@amcharts/amcharts4-geodata/australiaLow";
import am4geodata_chinaLow from "@amcharts/amcharts4-geodata/chinaLow";
import am4geodata_indiaLow from "@amcharts/amcharts4-geodata/indiaLow";
am4core.useTheme(am4themes_animated);


class Map extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      liveRegions: [],
      chart: null
    };
  }

  azureLocations = {
                      "asiaeast":      {  "region" : "Region : East Asia", 
                                          "location": "Location : Hong Kong",
                                          "latitude" : 22.3193,
                                          "longitude": 114.1694
                                       },
                      "uscentral":     { "region" : "Region : Central US", 
                                         "location": "Location : Iowa",
                                         "latitude" : 42.032974,
                                         "longitude": -95.581543
                                       },
                      "usdodcentral":  { "region" : "Region : US DoD Central", 
                                         "location": "Location : Iowa",
                                         "latitude" : 42.032974,
                                         "longitude": -93.581543
                                       },
                      "asiasoutheast": { "region" : "Region : Southeast Asia", 
                                         "location": "Location : Singapore",
                                         "latitude" : 1.290270,
                                         "longitude": 103.851959
                                       },
                      "australiac":    { "region" : "Region : Australia Central", 
                                         "location": "Location : Canberra",
                                         "latitude" : -23.69748,
                                         "longitude": 133.88362
                                       },
                      "australiac2":   { "region" : "Region : Australia Central 2", 
                                         "location": "Location : Canberra",
                                         "latitude" : -34.730194,
                                         "longitude": 135.850479
                                       },
                      "australiaeast": { "region" : "Region : Australia East", 
                                         "location": "Location : New South Wales",
                                         "latitude" : -27.470125,
                                         "longitude": 153.021072
                                       },
                      "australiasoutheast": { "region" : "Region : Australia Southeast", 
                                              "location": "Location : Victoria",
                                              "latitude" : -37.814,
                                              "longitude": 144.96332
                                            },
                      "brazilsouth":    { "region" : "Region : Brazil South", 
                                            "location": "Location : Victoria",
                                            "latitude" : -23.533773,
                                            "longitude": -46.625290
                                        },
                      "canadacentral":  { 
                                        "region" : "Region : Canada Central", 
                                        "location": "Location : Toronto",
                                        "latitude" : 51.617,
                                        "longitude": -85.933
                                        },
                      "canadaeast":     { 
                                          "region" : "Region : Canada East", 
                                          "location": "Location : Quebec City",
                                          "latitude" : 49.6499974,
                                          "longitude": -76.4833314
                                        },
                      "chinaeast":     { 
                                          "region" : "Region : China East", 
                                          "location": "Location : Shanghai",
                                          "latitude" : 31.22222,
                                          "longitude": 121.45806
                                       },
                      "chinae2":       {  
                                          "region" : "Region : China East 2", 
                                          "location": "Location : Shanghai",
                                          "latitude" : 30.71444,
                                          "longitude": 111.28472
                                       },
                      "chinanorth":    { 
                                        "region" : "Region : China North", 
                                        "location": "Location : Beijing",
                                        "latitude" : 39.913818,
                                        "longitude": 116.363625
                                     },
                      "chinan2":     {  
                                        "region" : "Region : China North 2", 
                                        "location": "Location : Beijing",
                                        "latitude" : 38.488800,
                                        "longitude": 106.249290
                                     },
                      "indiacentral":{  
                                      "region" : "Region : Central India", 
                                      "location": "Location : Pune",
                                      "latitude" : 28.65381,
                                      "longitude": 77.22897
                                     },
                      "indiasouth":  { 
                                      "region" : "Region : South India", 
                                      "location": "Location : Chennai",
                                      "latitude" : 13.08784,
                                      "longitude": 80.27847
                                     },
                      "indiawest":   {  
                                      "region" : "Region : West India", 
                                      "location": "Location : Mumbai",
                                      "latitude" : 19.07283,
                                      "longitude": 72.88261
                                     }

                                                                                                                    
                    }

  
  componentDidMount() {

    let chart = am4core.create("chartdiv", am4maps.MapChart);

    // Set map definition
    chart.geodata = am4geodata_worldLow;
    
    // Set projection
    chart.projection = new am4maps.projections.Miller();
    
    // Series for World map
    let worldSeries = chart.series.push(new am4maps.MapPolygonSeries());
    worldSeries.useGeodata = true;
    
    var polygonTemplate = worldSeries.mapPolygons.template;
    polygonTemplate.tooltipText = "{name}";
    polygonTemplate.fill = chart.colors.getIndex(0);
    polygonTemplate.nonScalingStroke = true;
    
    // Hover state
    let hs = polygonTemplate.states.create("hover");
    hs.properties.fill = am4core.color("#367B25");
    worldSeries.exclude = ["AQ"];
    //worldSeries.exclude = ["HK"];

    /**
     * Series for the U.S. map
     * It will use its own `geodata`
     */
    let usaSeries = chart.series.push(new am4maps.MapPolygonSeries());
    usaSeries.geodata = am4geodata_usaLow;
    usaSeries.useGeodata = true;
    polygonTemplate = usaSeries.mapPolygons.template;
    polygonTemplate.tooltipText = "{name}";
    polygonTemplate.fill = chart.colors.getIndex(0);
    polygonTemplate.nonScalingStroke = true;


     /**
     * Series for the Australia Map
     * It will use its own `geodata`
     */
    let australiaSeries = chart.series.push(new am4maps.MapPolygonSeries());
    australiaSeries.geodata = am4geodata_australiaLow;
    australiaSeries.useGeodata = true;
    polygonTemplate = australiaSeries.mapPolygons.template;
    polygonTemplate.tooltipText = "{name}";
    polygonTemplate.fill = chart.colors.getIndex(0);
    polygonTemplate.nonScalingStroke = true;

    /**
     * Series for the China Map
     * It will use its own `geodata`
     */
    let chinaSeries = chart.series.push(new am4maps.MapPolygonSeries());
    chinaSeries.geodata = am4geodata_chinaLow;
    chinaSeries.useGeodata = true;
    polygonTemplate = chinaSeries.mapPolygons.template;
    polygonTemplate.tooltipText = "{name}";
    polygonTemplate.fill = chart.colors.getIndex(0);
    polygonTemplate.nonScalingStroke = true;

    /**
     * Series for the India Map
     * It will use its own `geodata`
     */
    let indiaSeries = chart.series.push(new am4maps.MapPolygonSeries());
    indiaSeries.geodata = am4geodata_indiaLow;
    indiaSeries.useGeodata = true;
    polygonTemplate = indiaSeries.mapPolygons.template;
    polygonTemplate.tooltipText = "{name}";
    polygonTemplate.fill = chart.colors.getIndex(0);
    polygonTemplate.nonScalingStroke = true;


    this.setState({
        chart: chart
    });

    this.getAllRegions();
  }

  componentDidUpdate(prevProps, prevState)
  {
    if(this.state.isLoaded)
      this.renderGeoLocations(this.state.chart);
  }

  componentWillUnmount() {
  
  }

  renderGeoLocations(chart)
  {
    /*
      Adding markers
    */
    let imageSeries = chart.series.push(new am4maps.MapImageSeries());
    let imageSeriesTemplate = imageSeries.mapImages.template;
    let circle = imageSeriesTemplate.createChild(am4core.Circle);
    circle.radius = 4;
    circle.fill = am4core.color("#B27799");
    circle.stroke = am4core.color("#FFFFFF");
    circle.strokeWidth = 2;
    circle.nonScaling = true;
    circle.tooltipText = "{region} \n {location}";

    imageSeriesTemplate.propertyFields.latitude = "latitude";
    imageSeriesTemplate.propertyFields.longitude = "longitude";

    let imageSeriesData = [];
    console.log(this.state.liveRegions);
    for (var liveRegion of this.state.liveRegions)
    {
        imageSeriesData.push(this.azureLocations[liveRegion.GeoRegion]);
    }

    imageSeries.data = imageSeriesData;

    this.renderGeoPairLines(chart)
    
  }

  renderGeoPairLines(chart)
  {

    //var lineSeries = chart.series.push(new am4maps.MapLineSeries());

  }

  getAllRegions()
  {
    fetch("https://localhost:44303/api/region/all")
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          liveRegions: JSON.parse(result.content)
        });
      },
      // Note: it's important to handle errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
  }

  render() {
    return (
      <div id="chartdiv" style={{ width: "100%", height: "100%" }}></div>
    );
  }


}


export default Map;
